mapper.select:
select

from T_NODE_SOFT_MAP
where NODE_ID = #{nodeId,jdbcType=DECIMAL}
and NODE_TYPE = #{nodeType,jdbcType=VARCHAR}
and SOFT_NAME = #{softName,jdbcType=VARCHAR}

mapper.delete:
delete from T_NODE_SOFT_MAP
where NODE_ID = #{nodeId,jdbcType=DECIMAL}
  and NODE_TYPE = #{nodeType,jdbcType=VARCHAR}
  and SOFT_NAME = #{softName,jdbcType=VARCHAR}
